class Widget(ipyw.Widget):
    def __init__(self, name=None, **kwargs):
        if name:
            self.name = name
        super().__init__(**kwargs)

    def visible(self):
        self.layout.visibility = 'visible'

    def invisible(self):
        self.layout.visibility = 'hidden'

    def hide(self):
        self.layout.display = 'inline'

    def show(self):
        self.layout.display = 'none'

